(window.webpackJsonp=window.webpackJsonp||[]).push([[22],{PvZW:function(e,n,t){"use strict";t.r(n);var o=t("qXV1"),p=t.n(o),a={metaInfo:{titleTemplate:"%s - \u5feb\u901f\u4e0a\u624b"},data:function(){return{docs:p.a}}},s=t("KHd+"),l=Object(s.a)(a,function(){var e=this.$createElement,n=this._self._c||e;return n("div",{staticClass:"page--guide guide-quickstart  only-md-docs"},[n("ui-markdown",{staticClass:"markdown-page",attrs:{text:this.docs}})],1)},[],!1,null,null,null);n.default=l.exports},qXV1:function(e,n){e.exports='<h1 id="balmui-lite-\u5feb\u901f\u4e0a\u624b">BalmUI Lite \u5feb\u901f\u4e0a\u624b</h1>\n<p>\u597d\u7684\u5de5\u5177\u80fd\u8ba9\u5f00\u53d1\u66f4\u52a0\u7b80\u5355\u5feb\u6377\u3002</p>\n<p><a href="https://github.com/balmjs/balm-cli">Balm CLI</a> \u662f\u4e00\u4e2a\u547d\u4ee4\u884c\u754c\u9762\u5de5\u5177\uff0c\u5b83\u53ef\u4ee5\u5feb\u901f\u521b\u5efa\u57fa\u4e8e <a href="https://balmjs.com/">BalmJS</a> \u7684\u9879\u76ee\u811a\u624b\u67b6\u3002</p>\n<h2 id="1-\u8bbe\u7f6e\u5f00\u53d1\u73af\u5883">1. \u8bbe\u7f6e\u5f00\u53d1\u73af\u5883</h2>\n<p>\u5728\u5f00\u59cb\u5de5\u4f5c\u4e4b\u524d\uff0c\u6211\u4eec\u5fc5\u987b\u8bbe\u7f6e\u597d\u5f00\u53d1\u73af\u5883\u3002</p>\n<p>\u5982\u679c\u4f60\u7684\u673a\u5668\u4e0a\u8fd8\u6ca1\u6709 <a href="https://nodejs.org/en/download/">Node.js</a> \u548c npm\uff0c\u8bf7\u5148\u5b89\u88c5\u5b83\u4eec\u3002</p>\n<blockquote>\n<p>\u8bf7\u5148\u5728\u7ec8\u7aef/\u63a7\u5236\u53f0\u7a97\u53e3\u4e2d\u8fd0\u884c\u547d\u4ee4 <code>node -v</code> \u548c <code>npm -v</code>\uff0c \u6765\u9a8c\u8bc1\u4e00\u4e0b\u4f60\u6b63\u5728\u8fd0\u884c node 6.x.x \u548c npm 3.x.x \u4ee5\u4e0a\u7684\u7248\u672c\u3002 \u66f4\u8001\u7684\u7248\u672c\u53ef\u80fd\u4f1a\u51fa\u73b0\u9519\u8bef\uff0c\u66f4\u65b0\u7684\u7248\u672c\u5219\u6ca1\u95ee\u9898\u3002</p>\n</blockquote>\n<p>\u56e0\u4e3a <strong>BalmJS</strong> \u9700\u8981\u4f9d\u8d56 <strong>gulp</strong>\uff0c\u5982\u679c\u4e0d\u80fd\u8fd0\u884c <code>gulp -v</code>\uff0c\u8bf7\u5148\u5b89\u88c5 <strong>gulp-cli</strong>\u3002</p>\n<pre><code class="language-sh">npm install -g gulp-cli</code></pre>\n<p>\u7136\u540e\u5168\u5c40\u5b89\u88c5 Balm CLI \u3002</p>\n<pre><code class="language-sh">npm install -g balm-cli\n\n# \u9a8c\u8bc1\nbalm -V\n\n# \u8f93\u51fa\u7ed3\u679c\uff1a0.1.0</code></pre>\n<h2 id="2-\u521b\u5efa\u65b0\u9879\u76ee">2. \u521b\u5efa\u65b0\u9879\u76ee</h2>\n<p>\u6253\u5f00\u7ec8\u7aef\u7a97\u53e3\u3002</p>\n<p>\u8fd0\u884c\u4e0b\u5217\u547d\u4ee4\u6765\u751f\u6210\u4e00\u4e2a\u65b0\u9879\u76ee\u4ee5\u53ca\u5e94\u7528\u7684\u9aa8\u67b6\u4ee3\u7801\uff1a</p>\n<pre><code class="language-sh">balm init vue my-project</code></pre>\n<h2 id="3-\u5b89\u88c5">3. \u5b89\u88c5</h2>\n<p>\u8fdb\u5165\u9879\u76ee\u5e76\u5b89\u88c5\u4f9d\u8d56\uff1a</p>\n<pre><code class="language-sh">cd my-project\n\nyarn\n# OR\nnpm install</code></pre>\n<blockquote>\n<p>\u8bf7\u8010\u5fc3\u7b49\u5f85\u3002 \u5b89\u88c5\u4f9d\u8d56\u9700\u8981\u82b1\u8d39\u4e00\u5b9a\u65f6\u95f4\uff0c\u5927\u591a\u6570\u65f6\u5019\u90fd\u662f\u5728\u5b89\u88c5\u90a3\u4e9b npm \u5305\u3002\u5982\u679c\u201c\u7f51\u7edc\u72b6\u51b5\u201d\u4e0d\u79d1\u5b66\uff0c\u8bf7\u8bbe\u7f6e<a href="https://npm.taobao.org/">\u6dd8\u5b9d\u6e90</a></p>\n</blockquote>\n<p>\u5b89\u88c5 <strong>BalmUI Lite</strong></p>\n<pre><code class="language-sh">yarn add balm-ui-lite\n# OR\nnpm i --save balm-ui-lite</code></pre>\n<h2 id="4-\u914d\u7f6e-vue-\u9879\u76ee">4. \u914d\u7f6e Vue \u9879\u76ee</h2>\n<h3 id="41-\u914d\u7f6e\u6837\u5f0f">4.1 \u914d\u7f6e\u6837\u5f0f</h3>\n<p>\u7f16\u8f91\u6837\u5f0f\u6587\u4ef6\uff1a<code>my-project/app/styles/global/_vendor.scss</code></p>\n<pre><code class="language-css">/* \u65b0\u589e BalmUI Lite \u6837\u5f0f\u5e93 */\n@import &#39;node_modules/balm-ui-lite/src/styles/balm-ui-lite.scss&#39;;</code></pre>\n<blockquote>\n<p>\u63a8\u8350\u4f7f\u7528 Sass \u5f15\u5165 <code>/path/to/balm-ui-lite.scss</code> \uff0c\u53ef\u4ee5\u4f7f\u7528 Sass \u53d8\u91cf\u5feb\u901f\u91cd\u5199\u5168\u5c40\u6216\u7ec4\u4ef6\u7684\u6837\u5f0f</p>\n</blockquote>\n<h3 id="42-\u914d\u7f6e\u811a\u672c">4.2 \u914d\u7f6e\u811a\u672c</h3>\n<p>\u7f16\u8f91\u811a\u672c\u6587\u4ef6\uff1a<code>my-project/app/scripts/main.js</code></p>\n<pre><code class="language-js">import Vue from &#39;vue&#39;;\nimport App from &#39;./views/layouts/app&#39;;\nimport BalmUI from &#39;balm-ui-lite&#39;; // \u5f15\u7528\n\nVue.config.productionTip = false;\nVue.use(BalmUI); // \u5b89\u88c5\n\nnew Vue({\n  el: &#39;#app&#39;,\n  components: { App },\n  template: &#39;&lt;app/&gt;&#39;\n});</code></pre>\n<h3 id="43-ui-\u5e93\u6240\u9700\u7684\u56fe\u7247\u548c\u5b57\u4f53">4.3 UI \u5e93\u6240\u9700\u7684\u56fe\u7247\u548c\u5b57\u4f53</h3>\n<p>\u4e0b\u8f7d <a href="https://mdl.balmjs.com/assets.zip">BalmUI Lite assets</a> \u5e76\u89e3\u538b\uff0c\u5c06\u5176\u4e2d\u7684 <em>images</em> \u548c <em>fonts</em> \u6587\u4ef6\u5939\u590d\u5236\u5230\u9879\u76ee\u4e2d\uff08<strong><code>/path/to/my-project/app</code></strong>\uff09</p>\n<blockquote>\n<p>\u66f4\u591a\u4f7f\u7528\u65b9\u5f0f\u548c\u514d\u4e0b\u8f7d\u8d44\u6e90\u7684\u65b9\u6cd5\u8bf7\u53c2\u7167 <a href="https://mdl.balmjs.com/#/guide/advanced">\u8fdb\u9636\u7528\u6cd5</a></p>\n</blockquote>\n<h2 id="5-\u5f00\u53d1\u8c03\u8bd5">5. \u5f00\u53d1\u8c03\u8bd5</h2>\n<pre><code class="language-sh">npm run dev</code></pre>\n<p>\u4fee\u6539\u4e00\u4e2a\u7ec4\u4ef6\uff0c\u7f16\u8f91 Vue \u6587\u4ef6\uff1a<code>my-project/app/scripts/views/components/hello.vue</code></p>\n<pre><code class="language-html">&lt;template&gt;\n  &lt;div class=&quot;hello&quot;&gt;\n    ...\n    &lt;!-- \u6dfb\u52a0\u4e00\u4e2a\u6d4b\u8bd5\u6309\u94ae --&gt;\n    &lt;ui-button @click=&quot;$alert(&#39;gg&#39;)&quot;&gt;\u70b9\u6211&lt;/ui-button&gt;\n  &lt;/div&gt;\n&lt;/template&gt;</code></pre>\n<h2 id="6-\u6784\u5efa\u548c\u90e8\u7f72">6. \u6784\u5efa\u548c\u90e8\u7f72</h2>\n<pre><code class="language-sh">npm run prod</code></pre>\n<blockquote>\n<p><a href="https://balmjs.com/">BalmJS</a> \u9ed8\u8ba4\u4f1a\u5c06\u9879\u76ee\u6784\u5efa\u5230 <code>dist</code> \u76ee\u5f55\u4e2d\uff0c\u4f60\u53ef\u4ee5\u6839\u636e\u5177\u4f53\u4e1a\u52a1\u9700\u6c42\u4fee\u6539\u6784\u5efa\u548c\u90e8\u7f72\u7684\u914d\u7f6e\uff08<code>my-project/config/balmrc.js</code>\uff09\u3002</p>\n</blockquote>\n'}}]);